// Nav
(function($){

  function Nav(el, options) {
    this.$el = el;
    this.options = options;
    this.init();
  }

  Nav.prototype = {
    init: function(options) {
      //reset options if case may be
      if (options !== undefined) this.options = options;

      var self = this;

      this.$nav = $(self.options.nav);
      this.$body = $(self.options.body);
      this.$subNav = $(self.options.subNav);
      //search
      this.$searchOverlay = $(self.options.searchOverlay);
      this.$searchHandle = $(self.options.searchHandle);
      this.$searchInput = $(self.options.searchInput);
      this.$searchClose = $(self.options.searchClose);
      this.$subNavGrandfather = $(self.options.subNavGrandfather);

      //setup initial stuff
      this.setup();

      //do it again after all the page assets finished loading
      //(height changes after loading the fonts)
      $(window).on('load', function(){
        self.setup();
      });

      //and do it again on resize
      $(window).on('resize', function(){
        self.setup();
      });

      //listen for clicks
      this.$el.on('click', function(e){
        self.navTrigger(e);
      });

      this.$subNav.on('click', function(e){
        self.subnavTrigger(e, $(this));
      });

      this.$searchHandle.on('click', function (e) {
        self.searchTrigger(e);
      });

      this.$searchClose.on('click', function (e) {
        e.preventDefault();
        self.closeSearch();
      });

      //close the submenu whan you click/touch outside the nav
      $('body').on('click touchstart', function (e) {
        if(self.$nav.find('.' + self.options.subNavClass).length && !$(e.target).closest('.nav').length) {
          self.subnavCloseAll();
        }
      });
    },

    setup: function(){
      var self = this;

      if (!self.$el.is(':visible')) this.$subNavGrandfather.parent().attr('style', '');

      //the nav is open
      if(this.$nav.hasClass(this.options.className)){
        if (!self.$el.is(':visible')) {
          this.closeNav();
        }
      }
    },

    getNavHeight: function () {
      var self = this;
      return '0';
    },

    navTrigger: function(e){
      e.preventDefault();
      this.$nav.hasClass(this.options.className) ? this.closeNav() : this.openNav();
    },

    subnavTrigger: function (e, el) {
      e.preventDefault();
      var parent = el.closest(this.options.subNavParent);
      this.$subNav.not(el).closest(this.options.subNavParent).removeClass(this.options.subNavClass);
      this.$nav.toggleClass(this.options.subNavParentClass);
      parent.toggleClass(this.options.subNavClass);
      if(parent.hasClass(this.options.subNavClass)) {
        if (this.$el.is(':visible')) {
          this.$subNavGrandfather.parent().css('height','');
        }
        this.closeSearch();
      } else {
        if (this.$el.is(':visible')) {
          var height = 450-$('.social').height()-$('.subnav').height();
          this.$subNavGrandfather.parent().css('height',height);
        }
      }
    },

    subnavCloseAll: function () {
      this.$nav.removeClass(this.options.subNavParentClass);
      this.$nav.find('.' + this.options.subNavClass).removeClass(this.options.subNavClass);
    },

    openNav: function(){
      if (this.isAndroid()) $(".container").css("z-index", '-1');
      
      var self = this;
      this.closeSearch();
      self.$nav.addClass(self.options.className);
      var height = 450-$('.social').height()-$('.subnav').height();
//-+-+ Fix Bag of Pricelist      
      if (self.$nav.find(this.options.subNavGrandfather).parent().attr('class') != 'nav-scroll'){
      self.$nav.find(this.options.subNavGrandfather).parent().css('height',height+'px');
      }
      this.$el.addClass("is-expanded");
    },

    closeNav: function(){
      if (this.isAndroid()) $(".container").css("z-index", '0');
      
      var self = this;
      this.$body.removeClass(self.options.className);
      self.$nav.find(self.options.subNavGrandfather).parent().css('height', '');
      setTimeout(function(){
        self.$nav.removeClass(self.options.className);
      }, 200);
      this.$el.removeClass("is-expanded");
    },

    searchTrigger: function (e) {
      e.preventDefault();
      if(this.$searchHandle.hasClass(this.options.searchHandleClass)) {
        this.closeSearch();
      } else {
        this.openSearch();
      }
    },

    closeSearch: function () {
      this.$searchHandle.removeClass(this.options.searchHandleClass);
      this.$searchOverlay.removeClass(this.options.searchOverlayClass);
    },

    openSearch: function () {
      var self = this;
      $('body').removeClass("print_compare_models");
      $('#nav2 li a').removeClass("on");
      $('#bookaservice,#brochure,#quote,#testdrive,#compare_models,#compare_grades,#compare_filtering_models,#overlay_iframe').hide();
      
      this.$searchHandle.addClass(this.options.searchHandleClass);
      this.$searchOverlay.addClass(this.options.searchOverlayClass);
      //focus
      this.$searchInput.focus();
      //scroll top
      $("html, body").animate({ scrollTop: 0 }, 600);
    },
    
    isAndroid:function () {
      var agent = navigator.userAgent;
      var ag = agent.toLowerCase();
      if(ag.indexOf('android') != -1){
        return 1;
      }
      return 0;
    },
  };

  $.fn.nav = function(option) {
    return this.each(function() {
      var $this = $(this),
        options = $.extend({}, option);

      if (! $this.data('navInstance')) {
        $this.data('navInstance', new Nav($this, options));
      } else {
        $this.data('navInstance').init(options);
      }
    });
  };

})($$);
